const target=document.querySelectorAll("[data-anime]");function toggleMenu(){const e=document.getElementById("main-menu"),t=document.getElementById("sub-menu");e.classList.toggle("active"),t.classList.toggle("active")}function animeScroll(){const t=window.pageYOffset+(window.innerHeight+150);target.forEach(function(e){t>e.offsetTop?e.classList.add("animate"):e.classList.remove("animate")})}const debounce=(o,c,i)=>{let l;return function(...e){const t=this;var n=i&&!l;clearTimeout(l),l=setTimeout(function(){l=null,i||o.apply(t,e)},c),n&&o.apply(t,e)}};animeScroll(),window.addEventListener("scroll",debounce(()=>{animeScroll()},100));const buttoMobile=document.getElementById("button-mobile");buttoMobile.addEventListener("click",toggleMenu);